  providedIn: 'root'
})
export class UsersService {

  private httpClient = inject(HttpClient);
  private baseUrls: string;

  constructor() { 
    this.baseUrls = 'http://localhost:3001/api';
  }

  const httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': localStorage.getItem('token')?localStorage.getItem('token'):'NOTOKEN' // Agrega el token en la cabecera como 'Bearer <tu_token>'
    })
  };

  register(formValue: any){
    return firstValueFrom(
      this.httpClient.post<any>(`${this.baseUrls}/users/registro`, formValue)
    )
  }

  login(formValue: any){
    return firstValueFrom(
      this.httpClient.post<any>(`${this.baseUrls}/users/login`, formValue)
    )
  }

