import { Component } from '@angular/core';
import { FormGroup, FormControl  } from '@angular/forms';
import { Injectable, inject } from '@angular/core';
import { UsersService } from 'src/app/services/users.service';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

    formulario: FormGroup;

    userService = inject(UsersService);

    constructor() {

      this.formulario = new FormGroup({
        username: new FormControl(),
        email: new FormControl(),
        password: new FormControl(),
        rol: new FormControl(),
        id_datos: new FormControl()
      })
    }

    async onSubmit(){

      const response = await this.userService.register(this.formulario.value);
      //console.log(response);

    }

}
<div class="container-sm">
    <div class="row altura"></div>

    <div class="col-8 border shadow p-4 rounded rounded-5" >


        <form [formGroup]="formulario" (ngSubmit)="onSubmit()">

            <div class="mb-3">
                <label for="">Username</label>
                <input type="text" class="form-control" formControlName="username">
            </div>
            <div class="mb-3">
                <label for="">Email</label>
                <input type="text" class="form-control" formControlName="email">
            </div>
            <div class="mb-3">
                <label for="">Rol</label>
                <input type="text" class="form-control" formControlName="rol">
            </div>
            <div class="mb-3">
                <label for="">Datos</label>
                <input type="number" class="form-control" formControlName="id_datos">
            </div>
            <div class="mb-3">
                <label for="">Password</label>
                <input type="password" class="form-control" formControlName="password">
            </div>
            <input type="submit" value="Register" class="btn btn-info">

        </form>

    </div>

    <div class="row altura"></div>
</div>
